import { makeStyles } from '@material-ui/core/styles';

export default makeStyles({
  media: {
    height: 50,
    paddingTop: '70%',
    backgroundColor: 'rgba(222, 102, 128, 1)',

  },
  border: {
    border: 'solid',
  },
  fullHeightCard: {
    height: '100%',
  },
  card: {

    padding:'10px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    height: '100%',
    position: 'relative',
    backgroundColor: 'rgba(181, 221, 224, 1)'
  },



  cardActions: {
    padding: '0 px 8px 16px',
    display: 'flex',
    justifyContent: 'space-between',

  },
});
